import React from "react";
import { useState } from "react";
import publist from "../assets/JsonData/publicite-list.json";
import Badge from "../components/badge/Badge";
const Publicite = () => {
  const [openadd, setopenadd] = useState(false);
  const pubTableHead = ["", "location", "price", "size", "status"];
  const renderHead = (item, index) => <th key={index}>{item}</th>;
  const renderBody = (item, index) => (
    <tr key={index}>
      <td>{item.id}</td>
      <td>{item.location}</td>
      <td>{item.price}</td>
      <td>{item.size}</td>

      <td>
        <Badge type={pubStatus[item.status]} content={item.status} />
      </td>
      <td>{item.montant}</td>
    </tr>
  );
  const pubStatus = {
    disponible: "primary",
    indisponible: "danger",
  };
  const openAddPub = () => {
    setopenadd(true);
  };
  const closeAddPub = () => {
    setopenadd(false);
  };
  return (
    <div>
      <div>
        <h2 className="page-header">Publicité list :</h2>
        <div className="row">
          <div className="col-12">
            <button className="button col-2" onClick={openAddPub}>
              Add Publicité
            </button>
            <br />
            <br />
            {openadd ? (
              <div className="card col-6">
                <div className="card_body">
                  <label>Puclicite location</label>
                  <br />
                  <br />
                  <div className="col-10">
                    {" "}
                    <input
                      className="input"
                      type="text"
                      placeholder="location ..."
                    />
                  </div>
                  <label>Puclicite price</label>
                  <br />
                  <br />
                  <div className="col-10">
                    {" "}
                    <input
                      className="input"
                      type="text"
                      placeholder="price ..."
                    />
                  </div>
                  <label>Puclicite size</label>
                  <br />
                  <br />
                  <div className="col-10">
                    {" "}
                    <input
                      className="input"
                      type="text"
                      placeholder=" 4*4..."
                    />
                  </div>
                  <br />
                  <br />
                  <div className="row">
                    <div className="col-3">
                      {" "}
                      <button className="button col-8" onClick={closeAddPub}>
                        submit
                      </button>
                    </div>
                    <div className="col-3">
                      {" "}
                      <button className="button col-8" onClick={closeAddPub}>
                        cancel
                      </button>
                    </div>
                  </div>
                </div>
              </div>
            ) : (
              <div></div>
            )}
          </div>
        </div>
      </div>
    </div>
  );
};
export default Publicite;
