import React, { useState } from "react";
import { Wrapper, Form, Input, Button, Title } from "./LoginElements";
import axios from "axios";
import { useHistory } from "react-router-dom";
const Login = () => {
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");
  const history = useHistory();

  const doTheLogin = async () => {
    console.log(email, password);
    await axios
      .post("http://localhost:4000/users/login", {
        email: email,
        password: password,
      })
      .then((res) => {
        if (res.status == 201) {
          //  console.log("aaze");
          //    localStorage.setItem("auth", true);
          //      localStorage.setItem("id", res.data["employe"]["_id"]);
          //  localStorage.setItem("email", res.data["employe"]["email"]);

          history.push("/");
        }
        if (res.status == 422) {
          alert(`Invalid email or password`);
        }
        console.log(res);
      });
  };
  return (
    <>
      <Wrapper>
        <Form>
          <Title>Welcome</Title>
          <Input
            type="email"
            name="email"
            onChange={(e) => setEmail(e.target.value)}
            placeholder="email"
            value={email}
          />
          <Input
            onChange={(e) => setPassword(e.target.value)}
            type="password"
            name="password"
            placeholder="password"
            value={password}
          />
          <Button onClick={doTheLogin}>Login</Button>
        </Form>
      </Wrapper>
    </>
  );
};
export default Login;
